    checkChat(channel, userstate, message)

    let isCommand = REGEX_COMMAND.test(message);
    if (!isCommand) return;

    const [raw, command, argument] = message.toLowerCase().match(REGEX_COMMAND);

    const {response} = commands[command] || [];

